import React from 'react';
import { useState } from 'react';
import './index.scss';

const questions = [
  {
    title: 'React - это ... ?',
    variants: ['библиотека', 'фреймворк', 'приложение'],
    correct: 0,
  },
  {
    title: 'Компонент - это ... ',
    variants: ['приложение', 'часть приложения или страницы', 'то, что я не знаю что такое'],
    correct: 1,
  },
  {
    title: 'Что такое JSX?',
    variants: [
      'Это простой HTML',
      'Это функция',
      'Это тот же HTML, но с возможностью выполнять JS-код',
    ],
    correct: 2,
  },
];

function Result() {
  return (
    <div className="result">
      <img alt="pic" src="https://cdn-icons-png.flaticon.com/512/2278/2278992.png" />
      <h2>Вы отгадали 3 ответа из 3</h2>
      <button>Попробовать снова</button>
    </div>
  );
}

function Game({ question, step, setStep }) {
  const cor = question.correct;
  const [value, setValue] = useState(0);

  return (
    <>
      <div className="progress">
        <div style={{ width: '30%' }} className="progress__inner"></div>
      </div>
      <h1>{questions[step].title}</h1>
      <ul>
        <li onClick={() => setStep(step + 1)}>
          {question.variants[0]}
          {question.variants[2] == question.cor ? setValue(value + 1) : ''}
        </li>
        <li onClick={() => setStep(step + 1)}>
          {question.variants[1]}
          {question.variants[2] == question.cor ? setValue(value + 1) : ''}
        </li>
        <li onClick={() => setStep(step + 1)}>
          {question.variants[2]}
          {question.variants[2] === question.cor ? setValue(value + 1) : ''}
        </li>
      </ul>
    </>
  );
}

function App() {
  const [step, setStep] = useState(0);
  const question = questions[step];

  return (
    <div className="App">
      <Game question={question} step={step} setStep={setStep} />
      {/* <Result /> */}
    </div>
  );
}

export default App;
